//http://newsapi.org/v2/everything?q=apple&from=2021-01-05&sortBy=publishedAt&apiKey=3d0daabf73a4495da8e6b30d2f14bc13

import React, { useState, useEffect } from "react";
import "./AppleNews.css"

function AppleNews() {
    const [AppleNews, addAppleNews] = useState([]);

useEffect(() => {
 fetch(
    "http://newsapi.org/v2/everything?q=apple&from=2021-01-05&sortBy=publishedAt&apiKey=3d0daabf73a4495da8e6b30d2f14bc13"
 )
.then((response)  => response.json())
.then((data) => {
    console.log(data)
    const saba = data.articles;
    addAppleNews(saba)
})


},[])
console.log(AppleNews)

    return (
        <div className="allNewsWrapper">
            <div className="container">
                <div className="row h-100 align-items-center justify-content-center">
                    <div className="col-12 title-wrapper">
                   Latest News
                    </div>
                    <br></br>
                    {AppleNews.map((singleNews)=> {
                let newsTitle = singleNews.title;
                let newsLink = singleNews.url;
                let newsImage = singleNews.urlToImage;
                
                let newsWrapper = (
                    <div className="col-sm-12 col-md-4">
                <div className="singleNewsWrapper">
                   
                    <div className="newsThumbnail">
                      <a href={newsLink} target="_blank">
                        <img src={newsImage} />
                      </a>
                    </div>
                <div className="NewsInfoWrapper">
                    <div className="newsTitle"> 
                    <a href={newsTitle} target="_blank">
                    </a>
                  </div>
                  <div className="newsDesc">
                        {singleNews.description}
                      </div>
                  </div>

                  <div className="newsLink"> 
                    <a href={newsLink} target="_blank">
                    </a>
                  </div>
                  
                 
                   </div>
                 </div>   
                   );
                    return newsWrapper;
                    })}

                </div>

            </div>

        </div>
    )
}

export default AppleNews;
